package com.javalec.ex.SelfTest.N01;

public class VO {

	private int i;
	private int j;
	private String s1;
	private String s2;
	
	public VO() {
		// TODO Auto-generated constructor stub
	}
	
	public VO(int i, int j, String s1, String s2) {
		// TODO Auto-generated constructor stub
		this.i = i;
		this.j = j;
		this.s1 = s1;
		this.s2 = s2;
	}
	
	public int getI() {
		return i;
	}
	public void setI(int i) {
		this.i = i;
	}
	public int getJ() {
		return j;
	}
	public void setJ(int j) {
		this.j = j;
	}
	public String getS1() {
		return s1;
	}
	public void setS1(String s1) {
		this.s1 = s1;
	}
	public String getS2() {
		return s2;
	}
	public void setS2(String s2) {
		this.s2 = s2;
	}	
}
